import React, { useState } from "react";
import { TextField, Button, Box, Typography } from "@mui/material";

const SearchBar = ({ onSearch }) => {
  // State to handle individual fields
  const [searchFields, setSearchFields] = useState({
    name: "",
    date: "",
    politicalParty: "",
    keywords: "",
  });

  // Handle input changes
  const handleChange = (field) => (event) => {
    setSearchFields({
      ...searchFields,
      [field]: event.target.value,
    });
  };

  // Handle the search action
  const handleSearch = () => {
    if (onSearch) onSearch(searchFields);
  };

  return (
    <Box
      sx={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh", // Full screen height
        flexDirection: "column",

        padding: 3,
      }}
    >
      <Typography variant="h4" sx={{ marginBottom: 3 }}>
        Search Parliament Speeches
      </Typography>
      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
          gap: 2,
          width: "100%",
          maxWidth: "600px",
        }}
      >
        {/* Input for Member Name */}
        <TextField
          label="Name of Member"
          value={searchFields.name}
          onChange={handleChange("name")}
          variant="outlined"
          fullWidth
          InputProps={{ style: { fontSize: "1rem" } }}
          InputLabelProps={{ style: { fontSize: "1rem", color: "#e6e6e6" } }}
          sx={{ backgroundColor: "#ffffff", borderRadius: 1 }}
        />

        {/* Input for Date */}
        <TextField
          label="Date"
          value={searchFields.date}
          onChange={handleChange("date")}
          variant="outlined"
          fullWidth
          InputProps={{ style: { fontSize: "1rem" } }}
          InputLabelProps={{ style: { fontSize: "1rem", color: "#e6e6e6" } }}
          sx={{ backgroundColor: "#ffffff", borderRadius: 1 }}
        />

        {/* Input for Political Party */}
        <TextField
          label="Political Party"
          value={searchFields.politicalParty}
          onChange={handleChange("politicalParty")}
          variant="outlined"
          fullWidth
          InputProps={{ style: { fontSize: "1rem" } }}
          InputLabelProps={{ style: { fontSize: "1rem", color: "#e6e6e6" } }}
          sx={{ backgroundColor: "#ffffff", borderRadius: 1 }}
        />

        {/* Input for Keywords */}
        <TextField
          label="Keywords"
          value={searchFields.keywords}
          onChange={handleChange("keywords")}
          variant="outlined"
          fullWidth
          InputProps={{ style: { fontSize: "1rem" } }}
          InputLabelProps={{ style: { fontSize: "1rem", color: "#e6e6e6" } }}
          sx={{ backgroundColor: "#ffffff", borderRadius: 1 }}
        />

        {/* Search Button */}
        <Button
          variant="contained"
          onClick={handleSearch}
          sx={{
            fontSize: "1.2rem",
            padding: 1,
            backgroundColor: "#388ceb",
            "&:hover": { backgroundColor: "#1e66c1" },
          }}
        >
          Search
        </Button>
      </Box>
    </Box>
  );
};

export default SearchBar;
