import React, { useState } from "react";
import { TextField, Button, Box } from "@mui/material";

const SearchBar = ({ onSearch }) => {
  const [query, setQuery] = useState("");

  const handleSearch = () => {
    if (onSearch) onSearch(query);
  };

  return (
    <Box
      sx={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh", // Full screen height
        backgroundColor: "#4682B4", // Professional blue background
      }}
    >
      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
          gap: 2,
          alignItems: "center",
          width: "100%",
          maxWidth: "600px",
        }}
      >
        <TextField
          label="Search Speeches"
          variant="outlined"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          fullWidth
          sx={{
            backgroundColor: "#FFFFFF", // White input field for contrast
            color: "#000000", // Black text inside the input field for readability
            "& .MuiInputLabel-root": {
              color: "#000000", // Label color for contrast
            },
            "& .MuiOutlinedInput-root": {
              "& fieldset": {
                borderColor: "#000000", // Border color
              },
              "&:hover fieldset": {
                borderColor: "#000000", // Border color on hover
              },
              "&.Mui-focused fieldset": {
                borderColor: "#000000", // Border color when focused
              },
            },
            fontSize: "1.2rem", // Increase font size
            padding: "10px", // Add some padding inside the input field
          }}
        />
        <Button
          variant="contained"
          color="primary"
          onClick={handleSearch}
          disabled={!query.trim()}
          sx={{
            fontSize: "1.2rem", // Increase font size for the button
            padding: "12px 24px", // Increase padding for the button
            width: "100%", // Ensure the button fills the width
            backgroundColor: "#0077B6", // A brighter blue for the button
            "&:hover": {
              backgroundColor: "#005F8C", // Darker blue on hover
            },
          }}
        >
          Search
        </Button>
      </Box>
    </Box>
  );
};

export default SearchBar;
